method.code:
        protected function MakeTicketRef($iNextId)
        {
                switch(get_class($this))
                {
                        case 'UserRequest':
                        $sFormat = 'R-%06d';
                        break;

                        case 'Incident':
                        $sFormat = 'I-%06d';
                        break;

                        case 'Change':
                        case 'RoutineChange':
                        case 'EmergencyChange':
                        case 'NormalChange':
                        $sFormat = 'C-%06d';
                        break;

                        case 'Problem':
                        $sFormat = 'P-%06d';
                        break; 

						case 'RcxRequest':
						$sFormat = 'RCX-%06d';
						break;

						case 'BuildingRequest':
						$sFormat = 'ROM-%06d';
						break;

						case 'BuildingViolation':
						$sFormat = 'OVT-%06d';
						break;

						case 'HrRequest':
						$sFormat = 'HR-%06d';
						break;

                        default:
                        $sFormat = 'T-%06d';
                }
                return sprintf($sFormat, $iNextId);
        }
